mapper.select:
SELECT COUNT(*) FROM t_member_furniture

	
		member_id = #{member_id}
	
	
		AND furniture_id IN
		
			${furniture_id}

SELECT mf.index FROM t_member_furniture mf INNER JOIN t_furniture f ON mf.`furniture_id` = f.`ID`

	
		mf.member_id = #{member_id}
	
	
		AND mf.furniture_id = #{furniture_id}
	
	
		AND f.type = #{furniture_type}
