mapper.select:
SELECT * FROM v_member_deckport
WHERE member_id = #{member_id}
AND ID = #{deck_id}

mapper.update:
UPDATE t_member_deckport SET
	SHIP = #{ship}
WHERE
	member_id = #{member_id} AND ID = #{deck_id}

UPDATE t_member_deckport SET
	MISSION_STATUS = #{mission_status},
	MISSION_ID = #{mission_id},
	MISSION_COMPLETE_TIME = #{mission_complete_time},
	MISSION_FLAG = #{mission_flag}
WHERE member_id = #{member_id} AND ID = #{deck_id}

UPDATE t_member_deckport
SET lock = #{lock}
WHERE member_id = #{member_id} AND ID = #{deck_id}

UPDATE t_member_deckport
SHIP = #{deckport.ship,typeHandler=LongJsonArrayHandler}, MISSION_STATUS = #{deckport.mission_status}, MISSION_ID = #{deckport.mission_id}, MISSION_COMPLETE_TIME = #{deckport.mission_complete_time}, MISSION_FLAG = #{deckport.mission_flag}, lock = #{deckport.lock},
WHERE member_id = #{deckport.member_id} AND ID = #{deckport.deck_id}
